mapper.select:
select l.*,itemp.*,gtemp.*,(REPLACE(l.contractAmount,',','')-gtemp.payAmountTotal) noPayAmountTotal from labourservicefee l LEFT JOIN ( select SUM(REPLACE(amount,',','')) amountTotal,SUM(REPLACE(taxAmount,',','')) taxAmountTotal,SUM(REPLACE(taxPriceAmount,',','')) taxPriceAmountTotal,labourServiceFeeId labourServiceFeeId_1 from labourserviceinvoice i GROUP BY i.labourServiceFeeId ) itemp on l.labourServiceFeeId=itemp.labourServiceFeeId_1 LEFT JOIN ( select SUM(REPLACE(payAmount,',','')) payAmountTotal,labourServiceFeeId labourServiceFeeId_2 from labourservicegeld g GROUP BY g.labourServiceFeeId ) gtemp ON l.labourServiceFeeId=gtemp.labourServiceFeeId_2 where 0=0
and l.projectId = #{projectId}
order by l.createDate desc,l.signingDate desc

select count(*) from labourServiceFee i
and 0=0
and projectId = #{projectId}
